package org.lina.achatvoiture.controllers;

import org.lina.achatvoiture.controller.VoitureController;
import com.fasterxml.jackson.databind.ObjectMapper;
import org.hamcrest.Matchers;
import org.junit.jupiter.api.Test;
import org.lina.achatvoiture.dto.VoitureDto;
import org.lina.achatvoiture.service.VoitureService;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.boot.test.autoconfigure.web.servlet.AutoConfigureMockMvc;
import org.springframework.boot.test.autoconfigure.web.servlet.WebMvcTest;
import org.springframework.boot.test.mock.mockito.MockBean;
import org.springframework.http.MediaType;
import org.springframework.test.web.servlet.MockMvc;

import static org.hamcrest.Matchers.is;
import static org.mockito.Mockito.when;
import static org.springframework.test.web.servlet.request.MockMvcRequestBuilders.*;
import static org.springframework.test.web.servlet.result.MockMvcResultMatchers.jsonPath;
import static org.springframework.test.web.servlet.result.MockMvcResultMatchers.status;

@AutoConfigureMockMvc(addFilters = false)
@WebMvcTest(VoitureController.class)
class VoitureControllerTest {
    @Autowired
    private MockMvc mockMvc;

    @MockBean
    private VoitureService voitureService;

    @Autowired
    private ObjectMapper objectMapper;

    @Test
    public void testGetVoiture() throws Exception {
        VoitureDto voitureDto = new VoitureDto(
                1,
                "Ferrari",
                "Mondial",
                "",
                1985,
                300000,
                150,
                "",
                8,
                "Minicompact Cars",
                "essenceRegulier",
                "manuel",
                "6",
                "data:image/jpeg;base64,"
        );

        when(voitureService.getVoitureByMarque("Mondial")).thenReturn(voitureDto);

        mockMvc.perform(get("/api/voitures/get/Mondial")
                        .contentType(MediaType.APPLICATION_JSON))
                .andExpect(status().isOk())
                .andExpect(jsonPath("$.id", is(1)))
                .andExpect(jsonPath("$.marque", is("Ferrari")));
    }

    @Test
    public void testCreateVoiture() throws Exception {
        VoitureDto voitureDto = new VoitureDto(
                1,
                "Ferrari",
                "Mondial",
                "",
                1985,
                300000,
                150,
                "",
                8,
                "Minicompact Cars",
                "essenceRegulier",
                "manuel",
                "6",
                "data:image/jpeg;base64,"
        );

        when(voitureService.saveVoiture(voitureDto)).thenReturn(voitureDto);
        String jsonContent = objectMapper.writeValueAsString(voitureDto);

        mockMvc.perform(post("/api/voitures/create")
                        .content(jsonContent)
                        .contentType(MediaType.APPLICATION_JSON))
                .andExpect(status().isCreated())
                .andExpect(jsonPath("$.id", is(1)))
                .andExpect(jsonPath("$.model", is("Mondial")));
    }

    @Test
    public void testUpdateVoiture() throws Exception {
        VoitureDto oldVoitureDto = new VoitureDto(
                1,
                "BMW",
                "I3",
                "",
                2010,
                300000,
                150,
                "",
                8,
                "Minicompact Cars",
                "essenceRegulier",
                "manuel",
                "6",
                "data:image/jpeg;base64,"
        );

        VoitureDto voitureDto = new VoitureDto(
                1,
                "Ferrari",
                "Mondial",
                "",
                1985,
                300000,
                150,
                "",
                8,
                "Minicompact Cars",
                "essenceRegulier",
                "manuel",
                "6",
                "data:image/jpeg;base64,"
        );

        when(voitureService.updateVoiture(voitureDto)).thenReturn(voitureDto);

        mockMvc.perform(put("/api/voitures/update")
                        .contentType(MediaType.APPLICATION_JSON)
                        .content(objectMapper.writeValueAsString(voitureDto)))
                .andExpect(status().isOk())
                .andExpect(jsonPath("$.marque", is("Ferrari")))
                .andExpect(jsonPath("$.id", is(1)))
                .andExpect(jsonPath("$.annee", Matchers.not(2010)));
    }
}
